export const SHOW_PICTURE = "SHOW_PICTURE";
export const LOAD_PICTURE = "LOAD_PICTURE";
export const ERROR_PICTURE = "ERROR_PICTURE";


export const onLoadPicture = (description) => ({
    type: LOAD_PICTURE,
    payload: description
})
export const showPicture = (item) => ({
  type: SHOW_PICTURE,
  payload: item,
});

export const onLoadError = (error) => ({
    type: ERROR_PICTURE,
    payload: error
})


const checkResponse = (res) => {
    return res.ok ? res.json() : res.json().then((err) => Promise.reject(err));
  };

export const getPicture = (data) =>  {
    return function (dispatch){
        dispatch({type: LOAD_PICTURE});
        fetch(`Search ${data.promt}..`)
          .then(checkResponse)
          .then((result) => {
            dispatch({type: ORDER_SUCCESS,
              payload: result.order.number});
          })
          
          .catch(() => dispatch({type: ERROR_PICTURE}));}

}
