import "./interface.css";
import * as THREE from 'three';
import { useState, useRef, useEffect } from "react";
import { Configuration, OpenAIApi } from "openai";


export default function Dalle(){

  const [prompt, setPrompt] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);
  const imageRef = useRef(null);
  const [placeholder, setPlaceholder] = useState(
    "Search Bears with Paint Brushes the Starry Night, painted by Vincent Van Gogh.."
  );
  const configuration = new Configuration({
    apiKey: "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",
  });

  const openai = new OpenAIApi(configuration);

  const generateImage = async () => {
    setPlaceholder(`Search ${prompt}..`);
    setLoading(true);
    const res = await openai.createImage({
      prompt: prompt,
      n: 1,
      size: "512x512",
    });
    
    setLoading(false);
    setImageUrl(res.data.data[0].url);
  };

  useEffect(() => {
    const texture = new THREE.Texture(imageRef.current);
    texture.needsUpdate = true;

    const canvas = canvasRef.current;
    const scene = new THREE.Scene();
    
    const renderer = new THREE.WebGLRenderer({ canvas: canvas });

    const geometry = new THREE.BoxGeometry(1, 1, 1);
    const material = new THREE.MeshBasicMaterial({ map: texture });
    const cube = new THREE.Mesh(geometry, material);
    scene.add(cube);

    const animate = () => {
        requestAnimationFrame(animate);
        renderer.render(scene, camera);
    };
  
    animate();
 
    // Use texture in your scene here
  }, [canvasRef, imageUrl]);


 
  return (
    <div className="app-main">
      {loading ? (
        <>
          <h2>Generating..Please Wait..</h2>
          <div className="lds-ripple">
            <div></div>
            <div></div>
          </div>
        </>
      ) : (
        <>
          <h2>Generate an Image using Open AI API</h2>

          
          {imageUrl.length > 0 ? (
              
              <canvas ref={canvasRef} />
          ) : (
            <><textarea
            className="app-input"
            placeholder={placeholder}
            onChange={(e) => setPrompt(e.target.value)}
            rows="10"
            cols="40"
          />
          <button className="button-create " onClick={generateImage}>Generate an Image</button></>
          )}
        </>
      )}
    </div>
  );
}
